import React from "react";
import "./card.css";
import { PLACEHOLDER_CARDBACK_PATH, PLACEHOLDER_CARD_PATH } from "../../constants";

function Card({ id, name, isFlipped, onCardClick }) {
  const flippedClass = isFlipped ? " flipped" : "";

  const handleClick = () => {
    if (!isFlipped) {
      onCardClick(id);
    }
  };

  return (
    <div 
      className={`card${flippedClass}`} 
      data-logo={name} 
      onClick={handleClick}
    >
      <img
        src={PLACEHOLDER_CARDBACK_PATH}
        className="card-back"
        alt="card placeholder"
      />
      <img
        src={`${PLACEHOLDER_CARD_PATH}${name}.png`}
        className="card-front"
        alt="card"
      />
    </div>
  );
}

export default Card;
