import React from "react";
import "./card.css";
import { PLACEHOLDER_CARDBACK_PATH } from "../../constants";
import { PLACEHOLDER_CARD_PATH } from "../../constants";

function Card(props) {
  const { card, onClickCard, flipped} = props;

  var flippedClass = "";

  if (flipped) {
    flippedClass = "inative";
    classe = "";
  }

  const handleClickCaptureCard = (event) => {
    if (flipped) {
      event.stopPropagation();
    }
  };

  return (
    <div
    onClick={() => {
      onClickCard(card);
    }}
    onClickCapture={handleClickCaptureCard} className="card-front">
      <img
       src={PLACEHOLDER_CARDBACK_PATH}
        className="card-back"
        alt="card placeholder"
      />
      <img
        src={`${PLACEHOLDER_CARD_PATH}${card.name}.png`}
        className="card-front"
        alt="card"
      />
    </div>
  );
}

export default Card;
