import React, { useState, useEffect } from "react";
import "./game-panel.css";
import { Card } from "../index";
import { checkIfIsFlipped} from "../../helpers";

function GamePanel({ cards, selectedLevel, gameStarted}) {

  const [flippedCards, setFlippedCards] = useState([]);

  const gameClass =
    selectedLevel === "1"
      ? ""
      : selectedLevel === "2"
      ? "intermedio"
      : selectedLevel === "3"
      ? "avancado"
      :"";
      
      useEffect(() => {
        setFlippedCards([]);
      }, [gameStarted]);

      const handleClickCard = (card) => {
        let tmp = Array.from(flippedCards);
    
        if (gameStarted)
        {
          tmp.push(card);
          setFlippedCards(tmp);
        }
      };

  return (
    <section className="game-panel">
      <h3 className="sr-only">Pe√ßas do Jogo</h3>
      <div id="game" className={gameClass}>
        {cards.map((card) => (
          <Card 
            key={card.id} 
            name={card.name} 
            onClickCard={handleClickCard}
            flipped={checkIfIsFlipped(flippedCards, card.id)}
            />
        ))}
      </div>
    </section>
  );
}

export default GamePanel;
