import React from "react";
import "./card.css";
import { PLACEHOLDER_CARDBACK_PATH } from "../../constants";
import { PLACEHOLDER_CARD_PATH } from "../../constants";

function Card({ card, onClickCard, flipped, matched }) {
  const flippedClass = flipped ? " flipped" : "";
  const matchedClass = matched ? " inative" : "";
  const cardFrontClass = matched ? " grayscale" : "";
  
  const handleClickCaptureCard = (event) => {
    if (flipped) {
      event.stopPropagation();
    }
  };

  return (
    <div
      className={"card" + flippedClass + matchedClass}
      data-logo={card.name}
      onClick={() => { onClickCard(card); }}
      onClickCapture={handleClickCaptureCard}
    >
      <img
       src={PLACEHOLDER_CARDBACK_PATH}
        className="card-back"
        alt="card placeholder"
      />
      <img
        src={`${PLACEHOLDER_CARD_PATH}${card.name}.png`}
        className="card-front"
        alt="card"
      />
    </div>
  );
}

export default Card;
